INSERT INTO `wp_lux_options` VALUES("536", "_transient__tevolution_standalone_plugin", "a:5:{s:7:\"headers\";a:8:{s:4:\"date\";s:29:\"Tue, 18 Aug 2015 23:25:48 GMT\";s:6:\"server\";s:65:\"Apache Phusion_Passenger/4.0.10 mod_bwlimited/1.4 mod_fcgid/2.3.9\";s:13:\"last-modified\";s:29:\"Thu, 23 Apr 2015 12:29:43 GMT\";s:4:\"etag\";s:28:\"\"d168a63-4d81-514636f2b9bc0\"\";s:13:\"accept-ranges\";s:5:\"bytes\";s:14:\"content-length\";s:5:\"19841\";s:10:\"connection\";s:5:\"close\";s:12:\"content-type\";s:15:\"application/xml\";}s:4:\"body\";s:19841:\"<?xml version=\"1.0\" encoding=\"utf-8\"?>\n<templatic-plugin>\n	<templatic-standalone-plugin>\n		<plugin-name>Booking System</plugin-name>\n		<plugin-folder>Booking-system</plugin-folder>\n		<plugin-image>http://www.templatic.com/updates/images/booking-cal.png</plugin-image>\n		<plugin-description> <![CDATA[ This add-on is ideal in case you are looking to manage a hotel or vacation property with WordPress (and Tevolution). With it you get a fully functionality booking form, house and room functionality, booking calendar, tariff page, seasonal prices and more.]]></plugin-description>\n		<plugin-path>Templatic-Booking/templatic_booking.php</plugin-path>\n		<plugin-download-url>http://www.templatic.com/members</plugin-download-url>\n		<plugin-argument>BookingSystem</plugin-argument>\n		<plugin-price>$39.00</plugin-price>\n		<plugin-type>Other</plugin-type>\n	</templatic-standalone-plugin>\n	<templatic-standalone-plugin>\n		<plugin-name>Digital Download</plugin-name>\n		<plugin-folder>DigitalDownloads</plugin-folder>\n		<plugin-image>http://www.templatic.com/updates/images/icon-digital_download.png</plugin-image>\n		<plugin-description> <![CDATA[Use Digital Downloads add-on to showcase and sell digital products. Every product can be sold off-site (e.g. Amazon), on site (PayPal is built into DD) or using eJunkie. ]]></plugin-description>\n		<plugin-path>DigitalDownloads/dd.php</plugin-path>\n		<plugin-download-url>http://www.templatic.com/members</plugin-download-url>\n		<plugin-argument>digital_download</plugin-argument>\n		<plugin-price>$39.00</plugin-price>\n		<plugin-type>Other</plugin-type>\n	</templatic-standalone-plugin>\n	\n	<templatic-standalone-plugin>\n		<plugin-name>Appointment System</plugin-name>\n		<plugin-folder>appointment-system</plugin-folder>\n		<plugin-image>http://www.templatic.com/updates/images/icon-appointment.png</plugin-image>\n		<plugin-description> <![CDATA[ With this add-on you will be able to manage appointments for establishments such as doctor offices or spa salons. The add-on will allow you to generate a fully functional booking form and manage things like the staff and working hours.]]></plugin-description>\n		<plugin-path>appointment-system/appointment.php</plugin-path>\n		<plugin-download-url>http://www.templatic.com</plugin-download-url>\n		<plugin-argument>appointment_system</plugin-argument>\n		<plugin-price>$39.00</plugin-price>\n		<plugin-type>Other</plugin-type>\n	</templatic-standalone-plugin>\n	\n	<templatic-standalone-plugin>		\n		<plugin-name>Events </plugin-name>		\n		<plugin-folder>Tevolution-Events</plugin-folder>		\n		<plugin-image>http://www.templatic.com/updates/images/icon-event-manager.png</plugin-image>		\n		<plugin-description> <![CDATA[ Turn your Directory into an events portal where event organizers can submit event listings. Just like the regular listings, you will be able to charge for event submissions and monetize your site even further.  ]]></plugin-description>	\n		<plugin-path>Tevolution-Events/events.php</plugin-path>		\n		<plugin-download-url>http://templatic.com/plugins/directory-add-ons/wp-events-directory</plugin-download-url>		\n		<plugin-argument>tevolution_event_manager</plugin-argument>		\n		<plugin-price>$49.00</plugin-price>\n		<plugin-type>Directory</plugin-type>\n	</templatic-standalone-plugin>	\n	\n	<templatic-standalone-plugin>		\n		<plugin-name>Fields Monetization </plugin-name>		\n		<plugin-folder>Tevolution-FieldsMonetization</plugin-folder>		\n		<plugin-image>http://www.templatic.com/updates/images/icon-event-manager.png</plugin-image>		\n		<plugin-description> <![CDATA[ Control which listing packages get what fields with this amazing add-on. As an admin, you setup packages that can have exactly the fields you wish to offer on each of them. A great tool which can encourage people to go for a higher package that has more fields so they can add more content and details on their listings. ]]></plugin-description>		\n		<plugin-path>Tevolution-FieldsMonetization/fields_monetization.php</plugin-path>		\n		<plugin-download-url>http://templatic.com/plugins/directory-add-ons/tevolution-fields-monetization</plugin-download-url>		\n		<plugin-argument>templatic_fields_monetization</plugin-argument>		\n		<plugin-price>$49.00</plugin-price>\n		<plugin-type>Directory</plugin-type>\n	</templatic-standalone-plugin>	\n	\n	<templatic-standalone-plugin>		\n		<plugin-name>Multi Rating </plugin-name>		\n		<plugin-folder>Templatic-MultiRating</plugin-folder>		\n		<plugin-image>http://www.templatic.com/updates/images/icon-event-manager.png</plugin-image>		\n		<plugin-description> <![CDATA[ Allow visitors to leave category-specific multiple ratings with their reviews on listings. As admin, you can specify more than one rating option on listings. This means a person can for example rate a listing based on quality, friendliness of staff, hygiene and service. Customize it to add whatever ratings you wish to let users rate listing by. ]]></plugin-description>		\n		<plugin-path>Templatic-MultiRating/multiple_rating.php</plugin-path>		\n		<plugin-download-url>http://templatic.com/plugins/directory-add-ons/star-rating-plugin-multirating</plugin-download-url>		\n		<plugin-argument>templatic_multiple_rating</plugin-argument>		\n		<plugin-price>$39.00</plugin-price>\n		<plugin-type>Directory</plugin-type>\n	</templatic-standalone-plugin>	\n	\n	<templatic-standalone-plugin>		\n		<plugin-name>Admin Dashboard</plugin-name>		\n		<plugin-folder>Tevolution-AdminDashboard</plugin-folder>		\n		<plugin-image>http://www.templatic.com/updates/images/icon-event-manager.png</plugin-image>		\n		<plugin-description> <![CDATA[ Makes your life as admin more easier with extremely useful dashboard widgets. Get more information on your site\'s performance.  ]]></plugin-description>		\n		<plugin-path>Tevolution-AdminDashboard/admindashboard.php</plugin-path>		\n		<plugin-download-url>http://templatic.com/plugins/directory-add-ons/tevolution-plugin-admin-dashboard</plugin-download-url>		\n		<plugin-argument>dashboard_plugin_activate</plugin-argument>		\n		<plugin-price>$39.00</plugin-price>\n		<plugin-type>Directory</plugin-type>\n	</templatic-standalone-plugin>	\n	\n	<templatic-standalone-plugin>		\n		<plugin-name>Ad Manager</plugin-name>		\n		<plugin-folder>Templatic-AdManager</plugin-folder>		\n		<plugin-image>http://www.templatic.com/updates/images/icon-event-manager.png</plugin-image>		\n		<plugin-description> <![CDATA[ A powerful banner management system which lets you display ads on your pages, posts and listings. Banners can be city, category or listing-specific with many banner location available. Ad Manager also offers banner rotation so you can basically offer the same ad space more than once and make an even bigger profit. ]]></plugin-description>		\n		<plugin-path>Templatic-AdManager/admanager.php</plugin-path>		\n		<plugin-download-url>http://templatic.com/plugins/directory-add-ons/templatic-admanager-wordpress-plugin</plugin-download-url>		\n		<plugin-argument>ad_plugin_activate</plugin-argument>		\n		<plugin-price>$39.00</plugin-price>\n		<plugin-type>Directory</plugin-type>\n	</templatic-standalone-plugin>\n	\n	<templatic-standalone-plugin>		\n		<plugin-name>Duplicate Post Alert</plugin-name>		\n		<plugin-folder>Templatic-DuplicatePostAlert</plugin-folder>		\n		<plugin-image>http://www.templatic.com/updates/images/icon-event-manager.png</plugin-image>		\n		<plugin-des